sources.scss/everything.scss:
/* http://meyerweb.com/eric/tools/css/reset/ 
   v2.0 | 20110126
   License: none (public domain)
*/

$color1: #4b21ff; 

$color2: #272727; 

$color3: #fefefe; 

$color4: #a9a9a9; 

$color5: #bdbdbd; 

$project: #efefef; 

$hoverColor: #ffe621; 

$btnTransition: all 0.3s ease-in-out; 

html, body, div, span, applet, object, iframe,
h1, h2, h3, h4, h5, h6, p, blockquote, pre,
a, abbr, acronym, address, big, cite, code,
del, dfn, em, img, ins, kbd, q, s, samp,
small, strike, strong, sub, sup, tt, var,
b, u, i, center,
dl, dt, dd, ol, ul, li,
fieldset, form, label, legend,
table, caption, tbody, tfoot, thead, tr, th, td,
article, aside, canvas, details, embed, 
figure, figcaption, footer, header, hgroup, 
menu, nav, output, ruby, section, summary,
time, mark, audio, video {
	margin: 0;
	padding: 0;
	border: 0;
	font-size: 100%;
	font: inherit;
	vertical-align: baseline;
}
/* HTML5 display-role reset for older browsers */
article, aside, details, figcaption, figure, 
footer, header, hgroup, menu, nav, section {
	display: block;
}
body {
	line-height: 1;
}
ol, ul {
	list-style: none;
}
blockquote, q {
	quotes: none;
}
blockquote:before, blockquote:after,
q:before, q:after {
	content: '';
	content: none;
}
table {
	border-collapse: collapse;
	border-spacing: 0;
}


*{
    box-sizing: border-box;
}

body, 
html {
    font-size: 18px;
    background: $color1; 
    font-family: 'Fira Sans', sans-serif;
    height: 100%;

    .loading-bg{
        background: white;
        position: fixed;
        top: 0;
        left: 0;
        height: 100vh;
        width: 100vw;
        transform: translate(0, 100%); 
        z-index: 100;
    }

    .logo a{
        text-decoration: none;
        color: white;
    }
}

.menu-icon{
    position: fixed;
    right: 36px;
    top: 36px;
    padding: 20px;
    z-index: 11;

    .fas{
        font-size: 2rem;
        color: black; //$color1;
        cursor: pointer;
    }
}
.primary-btn{
    background: $color1;
    -webkit-border-radius: 30px;
    -moz-border-radius: 30px;
    border-radius: 30px;
    text-align: center;
    color: $color3;
    padding: 15px 30px;
    display: inline-block;
    text-transform: uppercase;
    letter-spacing: 3px;
    // margin-right: 15px;
    text-decoration: none;
    letter-spacing: 3px;
    transition: $btnTransition;
    &:hover{
        border-color: $hoverColor;
        background: $hoverColor;
        color: $color1;
        font-size: 1.5rem;
    }
}

.primary-btn, .ghost-btn{
    margin-right: 15px;
    margin-bottom: 20px;
}

.ghost-btn{
    border: 4px solid $color1;
    -webkit-border-radius: 30px;
    -moz-border-radius: 30px;
    border-radius: 30px;
    text-align: center;
    color: $color1;
    padding: 12px 27px;
    display: inline-block;
    text-transform: uppercase;
    letter-spacing: 3px;
    text-decoration: none;
    letter-spacing: 3px;
    transition: $btnTransition;
    &:hover{
        border-color: $hoverColor;
        color: $color1;
        font-size: 1.5rem;

    }

}

#menu{
    background: $color1; 
    position: fixed;
    width: 100%;
    top: 0;
    bottom: 0;
    transform: translate3d(-100%,0,0);
    z-index: 10;
    display: flex;
    align-items: center;
    justify-content: center;
    opacity: 1;
    transition: all .5s ease-in-out;

    nav{
        width: 400px;
        text-align: center;
    }
    a{
        font-size: 2.3rem;
        padding: 1rem;
        display: block;
        text-decoration: none;
        color: white;
        &:hover{
            color: $color5;
        }
    }
}

.menu-active{
    .menu-icon{
        .fas {
            color: white;
        }
    }
   #menu{
    width: 100%;
    top: 0;
    bottom: 0;
    transform: translate3d(0,0,0)
   }
} 

.scroll-section{
    height: 100vh; 
    overflow-y: scroll;
    overflow-x: hidden;
    padding: 56px;

}


.logo{
    border: 5px solid #fff;
    height: 100px; 
    width: 200px;
    color: white;
    display: flex;
    justify-content: center;
    align-items: center;
    font-size: 4rem;
    line-height: 0;
    font-family: 'Orbitron', sans-serif;
    text-transform: uppercase;
}

.info-section{
    width: 35%; 
    position: absolute;
    top: 0;
    bottom: 0;
    left: 0;
    padding: 54px;

    .info-box{

        position: absolute;
        bottom: 60px;

        h4{
            font-size: 1.2rem;
            color: $color3; 
            margin: 0.7rem 0;
        }

        p{
            font-size: 0.9rem;
            font-weight: 300;
            color: $color3;
            margin: 0.2rem 0 1.6rem;
        }
    }

    .icon-section{
        a{
            color: white;
            text-decoration: none;
            font-size: 2rem;
            padding: 0 10px;
            &:hover{
                color: $hoverColor; 
            }
            &:first-child{
                padding: 0 10px 0 0;
            }

        }
    }

}

#homee{
    .loading-bg{
        background: white;
        position: fixed;
        top: 0;
        left: 0;
        height: 100vh;
        width: 100vw;
        transform: translate(0, 100%); 
    }

    .info-section{
        width: 35%; 
        position: absolute;
        top: 0;
        bottom: 0;
