                box-shadow: 9px 20px 74px 6px rgba(0,0,0,0.34);
            }
        }

        .profile-section{
            vertical-align: top;
            display: inline-block;
            padding: 20% 0 0;
            max-width: 500px;

            .profile{
                h1{
                    font-size: 2.3rem;
                    font-weight: 500;
                    line-height: 1.2;
                    margin: 0 0 1rem; 
                    span{
                        color: $color1;
                    }
                }
                h5{
                    font-size: 1.1rem;
                    margin: 0 0 2rem 0;
                }
                p{
                    color: $color4;
                    font-weight: 300;
                    line-height: 1.4;
                    margin: 0 0 2rem;
                }
            }
        }
    }
}

@media (max-width: 767px) {
    #homee{
        .menu-icon{
            top: -3px;
            right: 20px;
            color: white;

            .fa{
                color: white;
            }
        }
        .content-area{
            height: auto;
            &:after{
                content: "";
                display: table;
                clear: both;
            }
        }
        .info-section{
            width: 30%;
            padding: 56px 30px;

            .icon-section{
                a{
                    font-size: 1.4rem;
                }
            }

            .logo{
                height: 50px;
                width: 70px;
                font-size: 1rem;
            }
            .info-box{
                display: none;
            }
        }
        .content-area{
            margin-left: 0;
            width: 100%;

            .img-container{
                padding: 56px 5px 0;
                width: 110px;

                .user-img{
                    position: static;
                    width: 100%;
                    height: calc(100% - 112px);
                }
            }
            .profile-section{
                width: 70%;
                float: right;
                padding: 20% 20px 0;


            }
        }
    }
}

@media (min-width: 768px) and (max-width:991px){
    #homee{
        .content-area{
            .img-container{
                padding: 56px 10px 0;
                .user-img{
                    height: calc(100% - 112px);
                    position: static;
                    width: 100%;
                }
            }
            .profile-section{
                width: 70%;
                float: right;
                padding: 20% 10px 0;
            }
        }
    }
}

@media (min-width: 992px) and (max-width: 1199px){
    #homee{
        .content-area{
            .img-container{
                padding: 56px 10px 0;
                float: left;
                .user-img{
                    height: calc(100% - 112px);
                    // height: 100%;
                    position: static;
