                &:hover{
                    -webkit-box-shadow: 0px 20px 74px 6px rgba(0,0,0,0.12);
                    -moz-box-shadow: 0px 20px 74px 6px rgba(0,0,0,0.12);
                    box-shadow: 0px 20px 74px 6px rgba(0,0,0,0.12);
                }

                .grey-bg{
                    background: $project;
                    position: absolute;
                    top: 0;
                    bottom: 0;
                    right: 0;
                    left: 0;
                    opacity: 0;
                    transition: all 0.3s ease-in-out; 
                    overflow: hidden;
                    &:hover{
                        opacity: 0.9; 
                        .project-info{
                            .project-details{
                                transform: translate3d(10px, 0, 0); 
                                opacity: 1;
                            }
                            .project-title{
                                opacity: 1;
                            }
                        }
                    }

                    .project-info{
                        padding: 56px;
                        position: absolute;
                        bottom: 0;
                        width: 100%;

                        .project-details{
                            position: relative;
                            font-size: 1.1rem;
                            text-transform: capitalize;
                            margin: 0 0 1rem;
                            transform: translate3d(-200px, 0, 0); 
                            transition: all .3s ease-in-out;
                            opacity: 0;
                        }
                        .project-title{
                            font-size: 1.8rem;
                            text-transform: uppercase;
                            font-weight: 700;
                            margin-left: 7px;
                            opacity: 0;
                        }
                        .fa-long-arrow-alt-right{
                            position: absolute;
                            bottom: 56px;
                            right: 56px;
                            font-size: 3rem;
                        }
                    }
                }


                h5{
                    font-weight: 600;
                    font-size: 1.3rem;
                    margin-bottom: 1.4rem;
                }

                p{
                    font-weight: 300;
                    font-size: 1rem; 
                    margin-bottom: 1rem;
                }
            }
        }
    }
}

