#contact{

    .info-section{
        width: 35%; 
        position: absolute;
        top: 0;
        bottom: 0;
        left: 0;
        padding: 54px;

        .info-box{

            position: absolute;
            bottom: 60px;

            h4{
                font-size: 1.2rem;
                color: $color3;  
                margin: 0.7rem 0;
            }

            p{
                font-size: 0.9rem;
                font-weight: 300;
                color: $color3;
                margin: 0.2rem 0 1.6rem;
            }
        }

        .icon-section{
            a{
                color: white;
                text-decoration: none;
                font-size: 2rem;
                padding: 0 10px;
                &:hover{
                    color: $hoverColor; 
                }
                &:first-child{
                    padding: 0 10px 0 0;
                }

            }
        }

    }
    .content-area{
        margin-left: 35%;
        width: 65%;
        background: $color3;
        height: 100%; 
        // padding: 0 0 0 25%;
        .img-container{
            display: inline-block;
            width: 25%;
            position: relative;
            height: 100%;

            .form-section{ 
                position: absolute;
                top: 56px;
                left: -250px;
                width: 650px;
                height: calc(100% - 112px);
                background: $color2;
                color: $color3;
                padding: 46px;
                -webkit-box-shadow: 9px 20px 74px 6px rgba(0,0,0,0.34);
                -moz-box-shadow: 9px 20px 74px 6px rgba(0,0,0,0.34);
                box-shadow: 9px 20px 74px 6px rgba(0,0,0,0.34);

                h4{
                    font-size: 1.3rem;
                    margin-top: 2rem;
                    margin-bottom: 4rem;
                }

                input[type="text"], 
                input[type="email"], 
                textarea{
                    width: 100%;
                    background: transparent;
                    border: none;
                    border-bottom: 2px solid $color4;
                    margin-bottom: 4rem;
                    font-size: 1.1rem;
                    text-transform: uppercase;
                    line-height: 1.2;
                    color: $color5; 
                    padding: 15px 0;
                }

                button[type="submit"]{
                    background: $color1;
                    border: none;
                    -webkit-border-radius: 30px;
                    -moz-border-radius: 30px;
                    border-radius: 30px;
                    font-size: 1rem;
                    text-align: center;
                    color: $color3;
                    padding: 17px 30px;
                    text-transform: uppercase;
                    width: 100%;
                    letter-spacing: 0.4rem;
                }
            }
        }

        .contact-section{
            vertical-align: top;
            display: inline-block;
            padding: 56px;
            width: 70%;

            .contact-info{
                h1{
                    font-size: 2.3rem;
                    font-weight: 500;
                    line-height: 1.2;
                    margin: 0 0 1rem; 
                    span{
                        color: $color1;
                    }
                }

                .contact-box{
                    max-width: 600px;
                    width: 100%;
                    padding: 30px;
                    transition: all 0.5s ease-in-out;
                    cursor: pointer;
                    &:hover{
                        -webkit-box-shadow: 0px 20px 74px 6px rgba(0,0,0,0.12);
                        -moz-box-shadow: 0px 20px 74px 6px rgba(0,0,0,0.12);
                        box-shadow: 0px 20px 74px 6px rgba(0,0,0,0.12);
                    }
                    h5{
                        font-weight: 600;
                        font-size: 1.1rem;
                        margin: 0 0 1.5rem;
                    }

                    p{
                        color: $color4;
                        font-weight: 300;
                        line-height: 1.4;
                        margin: 0 0 2rem;
                    }
                }
            }
        }
    }
}


