.container{
    .content-area{
        width: 70%;
        margin: 0 auto;

        .white-bg{
            background: white;
            padding: 20px;
        }

        h2{
            font-size: 3rem;
            margin-bottom: 2rem;
            text-align: center;
        }

        ul{
            display: grid;
            grid-template-columns: repeat(3, 1fr);
            gap: 20px;

            li{
                justify-self: center;
                align-self: center;
                position: relative;
                height: 400px;

                // .hover-bg{
                //     position: absolute;
                //     top: 0;
                //     left: 0;
                //     width: 100%;
                //     height: 100%;
                //     background: rgba(0,0,0,0.5);
                // }

                img{
                    opacity: 0.9;
                    width: 100%;
                    height: 400px;
                    object-fit: cover;
                    object-position: center;
                }

                img:hover{
                    opacity: 1;
                }
            }
        }
    }
}
